$(document).ready(function() {
    $('.change-background').on('change', function() {
       var $this = $(this);
       var value = $.trim($this.val());
       // toggleClass procura un valore booleano true, se il provide è true aggiungi classi, sennò le togli;
       $this.toggleClass('filled-background', value.length !== 0);
     }).change();
     // vogliamo quindi chiamare l'evento "change" su tutti gli output con la classe che switcha il BG;
   });
    